public static Object readObjectFrom(String path)
{
    ObjectInputStream ois = null;
    try
    {
        ois = new ObjectInputStream(new FileInputStream(path));
        Object o = ois.readObject();
        ois.close();
        return o;
    }
    catch (Exception e)
    {
        logger.warning("在从" + path + "读取对象时发生异常" + e);
    }
