for (i=0; i<16; i++) {
    sprintf(buf, "/dev/adc%d", i);
    fd[i] = open(buf, O_RDWR);
    if (fd[i] < 0) {
        perror(buf);
        return -1;
    }
    fcntl(fd[i], F_GETFD, &flags);
    flags |= O_NONBLOCK;
    fcntl(fd[i], F_SETFD, &flags);
