public static String md5Hex(String s) {
    if (s == null) {
        return null;
    }

    try {
        MessageDigest md5 = MessageDigest.getInstance("MD5");
        return hexEncode(md5.digest(s.getBytes(Constants.UTF_8)));
    } catch (Exception x) {
        throw new RuntimeException(x.getMessage(), x);
    }
}

