/**
 * @param string $where
 * @param mixed|null $value
 * @return $this
 */
public function where($where, $value = null) {
    if ($where && $value) {
        $parameterName = sha1($value);
        $this->where[] = sprintf('%s==%s', $where, ":$parameterName");
        $this->setParameter($parameterName, $value);
    } else {
        $this->where[] = $where;
    }

    return $this;
}
