int runInBackground(const char* cmd) {
	pid_t pid;
	pid = fork();
	if (pid == 0)
	{
        // child process
		system(cmd);
		wait();
		exit(1);
	} else if (pid < 0) {
        // fork failed
		printf("fork() failed!\n");
		return 0;
	}
