public List<CustomerSchedule> GetVisitingCustomers ()
{
    List<CustomerSchedule> visitingCustomers = new List<CustomerSchedule>();

    foreach ( Customer customer in customers )
    {
        float randomCustPerc = UnityEngine.Random.Range(0, 101) / 100f;
        float randomRestPerc = UnityEngine.Random.Range(0, 101) / 100f;

        if ( randomCustPerc < customer.VisitPercentage &&
            randomRestPerc < RestaurantInfo.Instance.VisitorsPercentage )
        {
            TimeData visitTime = GenerateVisitTime();
            CustomerSchedule cs = new CustomerSchedule(customer, visitTime);
            visitingCustomers.Add(cs);
        }
