[HttpPost]
public ActionResult Create(MessageViewModel model)
{
    var queue = GetQueue();
    var msg = new CloudQueueMessage(model.Message);
    queue.AddMessage(msg);

    ViewBag.Message = "Message added to queue!";
    return RedirectToAction("Create");
}
